import React from 'react';
import styled, {css} from 'styled-components';
import { Image, Flex, Icon } from '../../atoms';
import { TitleWithLink } from '../../molecules';
import { routes, text } from '../../../utils';
import { ReactComponent as Pointer } from '../../../assets/blackPointer.svg';
import meetTeamPhoto from '../../../assets/meetTeam.png'
import {media} from '../../../mixins';

const {desctopLG} = media;

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;

const MeetTeamOrganism = ({handleRedirectToPage}) => {
  return(
    <Container 
      column 
      paddingX='xl11' 
      marginY='xl131'
    >
      <Flex justifyEnd marginRight='pxl14'>
        <Image 
          src={meetTeamPhoto} 
          width='592px' 
          height='384px' 
          marginBottom='xl4'
        />
      </Flex>
      <Flex>
        <TitleWithLink 
          handleRedirectToPage={handleRedirectToPage} 
          link={routes.team} 
          color='darkBlue' 
          content={text.mainPage.teamLink}
          align='initial'
        >
          <Icon 
            SVG={Pointer} 
            width='6%' 
            height='5%' 
            marginLeft='xl5'
          />
        </TitleWithLink>
      </Flex>
    </Container>
  )
};

export default MeetTeamOrganism;
