import React from 'react';
import styled, {css} from 'styled-components';
import { TitleWithLink } from '../../molecules';
import {Flex, Typography, Icon} from '../../atoms';
import {routes, text} from '../../../utils';
import { ReactComponent as Pointer } from '../../../assets/pointer.svg';
import {media} from '../../../mixins';

const {desctopLG} = media;

const SizedText = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.sayHiText};  
  `}
`;

const SizedFlex = styled(Flex)`
  ${() => css`
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;

const StyledBlock = styled.div`
  ${({theme: {color}}) => css`
    background: ${color.darkBlue};  
  `}
`;

const FeaturedProjectOrganism = ({handleRedirectToPage}) => {
  return(
    <StyledBlock>
      <SizedFlex 
        column 
        paddingY='xl12' 
        paddingX='pxl11' 
        itemsEnd 
        marginTop='xl121'
      >
        <Flex>
          <TitleWithLink 
            handleRedirectToPage={handleRedirectToPage} 
            link={routes.projects} 
            content={text.featuredProjects.title} 
            color='white'
          >
            <Icon 
              SVG={Pointer} 
              width='6%' 
              height='5%' 
              marginLeft='xl5'
            />
          </TitleWithLink>
        </Flex>
        <SizedText 
          alignEnd 
          marginRight='xl11' 
          marginTop='xl3'
        >
          <Typography 
            family='sauceOne' 
            color='white' 
            size='xs'
          >
            {text.featuredProjects.description}
          </Typography>
        </SizedText>
      </SizedFlex>
    </StyledBlock>  
  )
};

export default FeaturedProjectOrganism;
