import React from "react";
import styled, { css } from 'styled-components';
import node, {bool, func} from 'prop-types';
import {Flex} from '../atoms';
import { Footer, Header } from "../molecules";
import {media} from '../../mixins';

const {desctopLG} = media;

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`

    height: 100vh;
    max-width: ${sizing.mainContainer};
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;
const MediaContainer = styled(Flex)`
  ${({theme: {color}}) => css`
    height: 100vh;
    background: ${color.gradient};
    ${desctopLG}{
      margin: auto;
    }
  `}
`;

const ModalWithGradientTemplate = ({
  isFooterShow, 
  handleOpenMenuModal, 
  children, 
  isThankYouModal,
  isTeamPage
}) => {
  return(
    <MediaContainer>
    <Container paddingX='xl11' column>
        <Header.SubHeader 
          handleOpenMenuModal={handleOpenMenuModal} 
          isThankYouModal={isThankYouModal}
          isTeamPage={isTeamPage}
        />
        {children}
        {isFooterShow &&
          <Footer/>
        }
    </Container>
    </MediaContainer>
    )
};

ModalWithGradientTemplate.propTypes = {
    isFooterShow: bool,
    handleOpenMenuModal: func,
    isThankYouModal: bool,
    children: node.isRequired,
}

export default ModalWithGradientTemplate;
