import React, { useState,  useEffect } from "react";
import {useHistory} from 'react-router-dom';
import {MenuModal} from '.';
import {Banner} from '../molecules';
import {
  FeaturedProjectOrganism, 
  AboutOrganism, 
  CommentsSliderOrganism, 
  WhatWeDoOrganism, 
  WeOfferOrganism, 
  BlogSliderOrganism, 
  MeetTeamOrganism
} from '../organisms';
import MainTemplate from '../templates/MainTemplate';

const MainPage = () => {

  const [isMenuClosed, setState] = useState(true);
  const history = useHistory();

  useEffect(() => {
    window.scrollTo(0, 0)
  }, [])

  const handleRedirectAndOpenMenuModal = (url) => () => {
    setState(!isMenuClosed);
    if(url) {
      history.push(url);
    }
  }

  return(
    <>
    { isMenuClosed ? (
      <>
        <MainTemplate 
          handleOpenMenuModal={handleRedirectAndOpenMenuModal} 
          handleRedirectToPage={handleRedirectAndOpenMenuModal} 
          isFooterShown={true} 
          isSayHiButtonShown={true} 
          isIconClickable={false}
        >
          <Banner.MainBanner/>
          <AboutOrganism/>
          <CommentsSliderOrganism/>
          <WhatWeDoOrganism/>
          <FeaturedProjectOrganism handleRedirectToPage={handleRedirectAndOpenMenuModal}/>
          <WeOfferOrganism/>
          <BlogSliderOrganism/>
          <MeetTeamOrganism handleRedirectToPage={handleRedirectAndOpenMenuModal}/>
        </MainTemplate>
      </>
      ) : <MenuModal 
            handleOpenMenuModal={handleRedirectAndOpenMenuModal} 
            handleRedirectToPage={handleRedirectAndOpenMenuModal}
          />
    }
    </>
  )
}

export default MainPage;
