import React, {useState, useEffect} from 'react';
import styled, {css} from 'styled-components';
import {useDropzone} from 'react-dropzone';
import {Typography, Input, Flex, Icon, Button} from '../../atoms';
import {links, text} from '../../../utils';
import { ReactComponent as Add } from '../../../assets/add.svg';
import { ReactComponent as File } from '../../../assets/blank-file.svg';
import { ReactComponent as Close } from '../../../assets/closeBlack.svg';

const SizedSide = styled(Flex)`
  ${() => css`
    max-width: 27vw;
  `}
`;

const SizedMain = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.sayHiInput};
  `}
`;

const style = {
  outline: 'none'
}
const StyledLabel = styled.label`
  ${() => css`
    cursor: pointer;
    outline: none;
  `}
`;

const StyledDiv = styled.div`
  ${() => css`
    display: flex;
    flex-direction: column;
    align-items: center;
  `}
`;

const StyledButton = styled(Button)`
  ${() => css`
    height: fit-content;
  `}
`;

const StyledClose = styled(Button)`
  ${() => css`
    display: flex;
    margin-left: -20px;
  `}
`;

const SideBlock = () => {
  return(
    <SizedSide column marginRight='xl3'>
      <Typography 
        size='xl' 
        color='darkBlue' 
        transform='uppercase' 
        marginBottom='xl5' 
        family='sauceSans'
      >
        {text.sayHi.sideBlock.title}
      </Typography>
      <Typography 
        size='xs' 
        color='darkBlue' 
        family='sauceOne'
      >
        {text.sayHi.sideBlock.description}
          <Typography 
            as="a" 
            href={links.email} 
            size='xs'
            color='darkBlue' 
            family='sauceOne'
          >
            {text.sayHi.sideBlock.link}
          </Typography>
        </Typography>
    </SizedSide>
  )
};

const MainBlock = ({isThanksClosed, setThanks}) => {
  const [isFileAdded, setState] = useState(false);
  const {getRootProps, getInputProps, acceptedFiles} = useDropzone({noDrag: true});
  const files = acceptedFiles.map(file => file.path);

  useEffect(() => {
    if(acceptedFiles.length > 0)
      setState(true);

  }, [acceptedFiles])

  const handleClick = () => {
    setState(false);
    acceptedFiles.length = 0;
  }

  return(
    <SizedMain 
      column 
      paddingBottom='xl7' 
      marginRight='xl9' 
      marginTop='xl12'
    >
      <Input 
        placeholder={text.sayHi.mainBlock.name} 
        marginBottom='xl6'
      />
      <Input 
        placeholder={text.sayHi.mainBlock.email} 
        marginBottom='xl6'
      />
      <Flex>
        <Input
          placeholder={text.sayHi.mainBlock.details}
          marginBottom='xl6'
        />
        {
          isFileAdded 
            ? <StyledClose
                onClick={handleClick}
              >          
                <Icon 
                  SVG={Close} 
                  width='40'
                  marginLeft='mxl'
                />
              </StyledClose>
            : <StyledLabel 
                for='addFile'
                onClick={null}
              >
                <Icon 
                  SVG={Add} 
                  width='14' 
                  height='40' 
                  marginLeft='mxl'
                />
              </StyledLabel>
        }
      </Flex>
      <Flex justifyBetween >
        <div>
          <div {...getRootProps({style})}>
            <input {...getInputProps()} id='addFile'/>
          </div>
          {isFileAdded? 
            <StyledDiv column itemsCenter>
              <Icon 
                SVG={File} 
                width='40' 
                height='70'
              />
              <Typography family='sauceOne' color='darkBlue'>{files}</Typography>
            </StyledDiv>
            : null
          }
        </div>
        <StyledButton 
          paddingY='xl' 
          paddingX='xl10' 
          marginRight='xl6' 
          color='redSalmon' 
          onClick={() => {setThanks(!isThanksClosed)}}
        >
        <Typography color='white' size='xs'>
          {text.sayHi.button}
        </Typography>
        </StyledButton>
      </Flex>
    </SizedMain>
  )
};

const SayHiBlock = {
  SideBlock,
  MainBlock,
};

export default SayHiBlock;
