import React from 'react';
import styled, { css } from 'styled-components';
import {Flex, Image, Typography } from '../../atoms';
import teamBannerPhoto from '../../../assets/main-photo-2.png';
import mainBannerPhoto from '../../../assets/main-photo-1.png';
import {text} from '../../../utils';
import {media} from '../../../mixins';

const {desctopLG, tabletLG} = media;

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};  
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;

const MediaImage = styled(Image)`
  ${() => css`
    ${tabletLG}{
      width: 64%;
    }
    ${desctopLG}{
      width: 66%;
    }
  `}
`;

const MainBanner = () => {
  return (
  <>
    <Container 
      paddingX='xl10' 
      display='block' 
      marginTop='xl10' 
      lineHeight='sm'
    >
      <Typography 
        transform="uppercase" 
        color="darkBlue" 
        size="xl2" 
        family='sauceSans'  
        margin='none' 
        marginLeft="xl9"
      >
        {text.mainPage.title.firstLine.normal} <em>{text.mainPage.title.firstLine.cursive}</em>
      </Typography>
      <Typography 
        align="right" 
        transform="uppercase" 
        color="darkBlue" 
        size="xl2" 
        family='sauceSans'  
        margin='none' 
        marginRight="xl9"
      >
        {text.mainPage.title.secondLine}
      </Typography>
    </Container>
    <Flex justifyEnd marginTop='xl9'>
      <MediaImage 
        src={mainBannerPhoto} 
        width='65%' 
        height='43vw'
      />
    </Flex>
  </>
  )
};

const SubBanner = () => {
  return (
    <>
      <Typography 
        transform="uppercase" 
        color="darkBlue" 
        size="xl2" 
        family='sauceSans' 
        marginLeft="xl9"
      >
        At the heart
      </Typography>
      <Typography 
        align="right" 
        transform="uppercase" 
        color="darkBlue" 
        size="xl2" 
        family='sauceSans' 
        marginRight="xl9"
      >
        of <em>all this</em>
      </Typography>
      <Typography 
        align="center" 
        transform="uppercase" 
        color="darkBlue" 
        size="xl2" 
        family='sauceSans'
      >
        is our people
      </Typography>
      <Image 
        src={teamBannerPhoto} 
        width='704px' 
        height='488px' 
        marginTop='xl9'
      />
    </>
  )
};

const Banner = {
  MainBanner,
  SubBanner,
};

export default Banner;
