import React, { useState } from "react";
import {useHistory} from 'react-router-dom';
import {MenuModal, ThanksModal} from '.';
import MainTemplate from '../templates/MainTemplate';
import {SayHiOrganism} from '../organisms';

const SayHiPage = () => {
  const [isMenuClosed, setState] = useState(true);
  const [isThanksClosed, setThanks] = useState(true);
  const history = useHistory();

  const handleOpenThanksModal = () => {
    setThanks(!isThanksClosed);
  }

  const handleRedirectAndOpenMenuModal = (url) => () => {
    setState(!isMenuClosed);
    if(url) {
      history.push(url);
    }
  }

  return(
    <>
      { isMenuClosed ? ( isThanksClosed ? <MainTemplate 
                                handleOpenMenuModal={handleRedirectAndOpenMenuModal} 
                                handleRedirectToPage={handleRedirectAndOpenMenuModal} 
                                isFooterShown={false} 
                                isSayHiButtonShown={false} 
                                isIconClickable={true}
                                isSayHiModal={true}
                              >
                              <SayHiOrganism 
                                isThanksClosed={isThanksClosed} 
                                setThanks={setThanks}
                              />
                            </MainTemplate>
                            : <ThanksModal 
                                isFooterShow={true} 
                                handleOpenThanksModal={handleOpenThanksModal} 
                                handleRedirectToPage={handleRedirectAndOpenMenuModal}
                              />
                  )
                : <MenuModal
                    handleRedirectToPage={handleRedirectAndOpenMenuModal} 
                    handleOpenMenuModal={handleRedirectAndOpenMenuModal}
                  />
      }  
    </>
)};

export default SayHiPage;
