const devices = {
    mobile: 375,
    tabletSM: 530,
    tabletLG: 780,
    desctop: 1440,
    desctopLG: 1440,
};

export default Object.keys(devices).reduce(
    (acc, device) => {
        if(device = 'desctopLG') acc[device] = `@media(min-width: ${devices[device]}px)`;
        else acc[device] = `@media(max-width: ${devices[device]}px)`;
        return acc;  
    }, {}
);
