import React from "react";
import { useSwipeable } from "react-swipeable";
import {
  Wrapper,
  CarouselContainer,
  CarouselSlot,
  PREV,
  NEXT
} from "./components";
import styled, {css} from 'styled-components';
import {HeaderWithArrows} from '../';

const StyledDiv = styled(CarouselSlot)`
  ${({theme: {sizing, spacing}, isBlogSlider, order}) => css`
    margin-left: ${spacing.xl5};
    display: flex;
    flex-direction: column;
    ${(isBlogSlider && order==0) && `
      width: ${sizing.dropdownWeDo};
      height: ${sizing.blogImageBig};
      margin-left: 0;
    `}
    ${(isBlogSlider && order!=0) && `
      height: ${sizing.blogImageSmall};
      max-width: calc((${sizing.getInTouch} + 20px));
    `}
  `}
`;

const getOrder = ({ index, pos, numItems }) => (index - pos < 0 ? numItems - Math.abs(index - pos) : index - pos);

const initialState = { pos: 0, sliding: false, dir: NEXT };

const Carousel = (props) => {
  const [state, dispatch] = React.useReducer(reducer, initialState);
  const numItems = React.Children.count(props.children);
  const slide = (dir) => {
    dispatch({ type: dir, numItems });
    setTimeout(() => {
      dispatch({ type: "stopSliding" });
    }, 50);
  };

  const handlers = useSwipeable({
    onSwipedLeft: () => slide(NEXT),
    onSwipedRight: () => slide(PREV),
    preventDefaultTouchmoveEvent: true,
    trackMouse: true
  });

  return (
    <div {...handlers}>
      <HeaderWithArrows 
        content={props.content} 
        leftBtn={() => slide(PREV)} 
        rightBtn={() => slide(NEXT)} 
        border={props.border}
      />
      <Wrapper>
        <CarouselContainer dir={state.dir} sliding={state.sliding}>
          {React.Children.map(props.children, (child, index) => (
            <StyledDiv 
              isBlogSlider={props.isBlogSlider}
              key={index}
              order={getOrder({ index: index, pos: state.pos, numItems })}
            >
              {child}
            </StyledDiv>
          ))}
        </CarouselContainer>
      </Wrapper>
    </div>
  );
};

const reducer = (state, { type, numItems }) => {
  switch (type) {
    case "reset":
      return initialState;
    case PREV:
      return {
        ...state,
        dir: PREV,
        sliding: true,
        pos: state.pos === 0 ? numItems - 1 : state.pos - 1
      };
    case NEXT:
      return {
        ...state,
        dir: NEXT,
        sliding: true,
        pos: state.pos === numItems - 1 ? 0 : state.pos + 1
      };
    case "stopSliding":
      return { ...state, sliding: false };
    default:
      return state;
  }
};

export default Carousel;
