import React, { useState, useEffect} from "react";
import {useHistory} from 'react-router-dom';
import {MenuModal} from '.';
import MainTemplate from "../templates/MainTemplate";
import {ProjectsOrganism} from "../organisms";

const ProjectPage = () => {
  const [isMenuClosed, setState] = useState(true);
  const history = useHistory();

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [])

  const handleRedirectAndOpenMenuModal = (url) => () => {
    setState(!isMenuClosed);
    if(url) {
      history.push(url);
    }
  }

  return(
    <>
      { isMenuClosed ? ( 
          <MainTemplate  
            handleOpenMenuModal={handleRedirectAndOpenMenuModal} 
            handleRedirectToPage={handleRedirectAndOpenMenuModal} 
            isFooterShown={true} 
            isSayHiButtonShown={true} 
            isIconClickable={true}
          >
            <ProjectsOrganism/>
          </MainTemplate>
        ) : <MenuModal 
              handleRedirectToPage={handleRedirectAndOpenMenuModal} 
              handleOpenMenuModal={handleRedirectAndOpenMenuModal}
            />
      }
    </>
  )
};

export default ProjectPage;
