import React from 'react';
import styled, {css} from 'styled-components';
import {Typography, Flex} from '../../atoms';

const StyledFlex = styled(Flex)`
  ${({theme: {color}}) => css`
    width: 55vw;
    border-top: 1px solid ${color.darkBlue};
    &:nth-child(1){
      border-top: none;
    }
  `}
`;

const SizedTypography = styled(Typography)`
  ${() => css`
    max-width: 26.5vw;
  `}
`;

const TypographyTitle = styled(Typography)`
  ${() => css`
    max-width: 26vw;
  `}
`;

const WeOffer = ({title, description}) => {
  return(
    <>
      <StyledFlex 
        itemsStart 
        justifyBetween 
        paddingY='xl5'
      >
        <TypographyTitle 
          family='sauceSans' 
          color='darkBlue' 
          size='md' 
          transform='uppercase'
        >
          {title}
        </TypographyTitle>
        <SizedTypography 
          family='sauceOne' 
          color='darkBlue' 
          size='xs'
        >
          {description}
        </SizedTypography>
      </StyledFlex>
    </>
  )
};

export default WeOffer;
