import React from 'react';
import styled, {css} from 'styled-components';
import { v4 as uuidv4 } from 'uuid';
import { Carousel, BlogSlide } from '../../molecules';
import { Flex } from '../../atoms';
import { text } from '../../../utils';
import blogOne from '../../../assets/blog-1.png';
import blogTwo from '../../../assets/blog-2.png';
import blogThree from '../../../assets/blog-3.png';
import {media} from '../../../mixins';

const {desctopLG} = media;

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};
    overflow: hidden;
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;

const items = [
  {
    image: blogOne,
    link: text.mainPage.blogSection.cards.first
  },
  {
    image: blogTwo,
    link: text.mainPage.blogSection.cards.second
  },
  {
    image: blogThree,
    link: text.mainPage.blogSection.cards.third
  },
];

const BlogSliderOrganism = () => {
  return (
    <Container 
      paddingX='xl11' 
      marginY='xl14' 
      column
    >
      <Carousel 
        border='1px solid black' 
        content={text.mainPage.blogSection.title} 
        isBlogSlider={true}
      >
        { items.map(item => <BlogSlide key={uuidv4()} items={item}/>)}
      </Carousel>
    </Container> 
  )
};

export default BlogSliderOrganism;
