import { css } from 'styled-components';

const lineHeight = ({ lineHeight, theme }) => {
  switch (lineHeight) {
    case 'none':
      return css`line-height: ${theme.lineHeight.none};`;
    case 'xs':
      return css`line-height: ${theme.lineHeight.xs};`;
    case 'sm':
      return css`line-height: ${theme.lineHeight.sm};`;
    default:
      return null;
  }
};

export default lineHeight;
