import React from "react";
import {useHistory} from 'react-router-dom';
import AboutApp from './AboutApp';
import '../style.css';

const MainPage = () => {
  const history = useHistory();

  const handleRedirect = (url) => () => {
    if(url) {
      history.push(url);
    }
  }

  return(
    <>
      <header>
        <div className="navbar">
          <h1 className="navbar-title">Main Page</h1>
        </div>
      </header> 
      <main>
        <section class="m-auto w-75 section d-flex justify-content-around flex-wrap align-content-center">
          <a href="#" className="router-link">Registration</a>
          <a href="#" className="router-link">Sign In</a>
          <button onClick={handleRedirect('/info')} className="router-link">User Info</button>
          <button className="router-link" onClick={handleRedirect('/about')}>
            About App
          </button>
          <a href="#" className="router-link">Calculator</a>
        </section>
      </main>
    </>
  )
};

export default MainPage;
