import { node } from 'prop-types';
import styled from 'styled-components';
import { flexbox, marginMixin, paddingMixin, lineHeight } from '../../../mixins/index';

const {
  flex,
  flexDisplay,
  flexDirection,
  flexWrap,
  alignContent,
  alignItems,
  alignSelf,
  justifyContent,
} = flexbox;

const {
  margin,
  marginTop,
  marginRight,
  marginBottom,
  marginLeft,
  marginX,
  marginY,
} = marginMixin;

const {
  padding,
  paddingTop,
  paddingRight,
  paddingBottom,
  paddingLeft,
  paddingX,
  paddingY,
} = paddingMixin;

const Flex = styled.div`
  ${alignContent}
  ${alignItems}
  ${alignSelf}
  ${flex}
  ${flexDirection}
  ${flexDisplay}
  ${flexWrap}
  ${justifyContent}

  ${margin}
  ${marginTop}
  ${marginRight}
  ${marginBottom}
  ${marginLeft}
  ${marginX}
  ${marginY}

  ${padding}
  ${paddingTop}
  ${paddingRight}
  ${paddingBottom}
  ${paddingLeft}
  ${paddingX}
  ${paddingY}

  ${lineHeight}
`;

Flex.propTypes = {
  children: node.isRequired,
};

export default Flex;
