import React from "react";
import axios from 'axios';
import API from '../api';

import '../style.css';

export default class InfoUser extends React.Component {
  state = {
    persons: []
  }

  handleSubmit = async event => {
    event.preventDefault();
    const response = await API.get(`https://jsonplaceholder.typicode.com/users`)
    this.setState(response.data);
}

  render() {
    return (
      <>
        <header>
          <div className="navbar">
            <h1 className="navbar-title">User Info</h1>
          </div>
        </header> 
        <main>
          <section className="info-section">
            <div className="about-content">
              <div className="user-photo"></div>
              <div className="user-description">
                <h2 className="about-title">
                  {console.log(this.state.persons.name)}
                </h2>
              </div>
            </div>
            <table className="user-table">
                  <thead className="table-head">
                    <tr>
                      <th className="column">Email</th>
                      <th>Gender</th>
                      <th>Date of Birth</th>
                    </tr>
                  </thead>
                  <tbody className="table-body">
                    <tr>
                      <td className="column">email@gmail.com</td>
                      <td>Male</td>
                      <td>14.04.93</td>
                    </tr>
                  </tbody>
                </table>
          </section>
        </main>
      </>
    )
  }
};


