import styled, { css } from 'styled-components';
import { marginMixin } from '../../../mixins';

const {
  margin,
  marginTop,
  marginRight,
  marginBottom,
  marginLeft,
  marginX,
  marginY,
} = marginMixin;

const Image = styled.div`
  ${({src,
    width,
    height,
  }) => css`
    ${margin}
    ${marginTop}
    ${marginRight}
    ${marginBottom}
    ${marginLeft}
    ${marginX}
    ${marginY}

    width: ${width};
    height: ${height};
    background: url("${src}") 100% 100% no-repeat;
    background-size: cover;
  `}
`;

export default Image;
