import React from 'react';
import styled, {css} from 'styled-components';
import { v4 as uuidv4 } from 'uuid';
import { Flex } from '../../atoms';
import { Carousel, CommentSlide } from '../../molecules';
import { text } from '../../../utils';
import {media} from '../../../mixins';

const {desctopLG} = media;

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};
    overflow: hidden;
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;

const items = [
  {
    description: text.mainPage.slider.feedback.first.description,
    link: text.mainPage.slider.feedback.first.link,
    person: text.mainPage.slider.feedback.first.person,
    work: text.mainPage.slider.feedback.first.work
  },
  {
    description: text.mainPage.slider.feedback.second.description,
    link: text.mainPage.slider.feedback.second.link,
    person: text.mainPage.slider.feedback.second.person,
    work: text.mainPage.slider.feedback.second.work
  },
  {
    description: text.mainPage.slider.feedback.third.description,
    link: text.mainPage.slider.feedback.third.link,
    person: text.mainPage.slider.feedback.third.person,
    work: text.mainPage.slider.feedback.third.work
  },

];

const CommentsSliderOrganism = () => {
  return (
    <Container 
      paddingX='xl11' 
      marginY='xl14' 
      column
    >
      <Carousel content={text.mainPage.slider.title}>
        {items.map(item => <CommentSlide key={uuidv4()} items={item}/>)}
      </Carousel>
    </Container> 
  )
};

export default CommentsSliderOrganism;
