import React from "react";
import styled, { css } from 'styled-components';
import node, {bool, func} from 'prop-types';
import {Flex} from '../atoms';
import { Header } from "../molecules";
import {GetInTouchOrganism} from '../organisms';
import {media} from '../../mixins';

const {desctopLG} = media;

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};
    ${desctopLG}{
      margin: auto;
    }
  `}
`;

const MainTemplate = ({
  handleOpenMenuModal, 
  handleRedirectToPage, 
  children, 
  isFooterShown, 
  isSayHiButtonShown, 
  isIconClickable, 
  isSayHiModal
}) => {
  return(
    <>
      <Container paddingX='xl11' column>
          <Header.MainHeader 
            isSayHiModal={isSayHiModal}
            handleOpenMenuModal={handleOpenMenuModal} 
            handleRedirectToPage={handleRedirectToPage} 
            isSayHiButtonShown={isSayHiButtonShown} 
            isIconClickable={isIconClickable}
          /> 
      </Container>
      {children}
      { isFooterShown &&
        <GetInTouchOrganism handleRedirectToPage={handleRedirectToPage}/>
      }
    </>
  )
};

MainTemplate.propTypes = {
    isSayHiButtonShown: bool,
    isFooterShown: bool,
    handleOpenMenuModal: func,
    handleRedirectToPage: func,
    isIconClickable: bool,
    children: node.isRequired,
}

export default MainTemplate;
