import React, { useMemo } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { DropdownRow } from '../../molecules';

const DROPDOWN_ROW_INDEX_TO_BE_OPENED_BY_DEFAULT = 0;

const DropdownOrganism = ({
  typeOfHeading, 
  dropdownHeadingsArray, 
  dropdownContentArray, 
  withRowOpeningOnScroll
}) => {

  const dropdownItems = useMemo(() => 
    dropdownHeadingsArray && dropdownHeadingsArray.map((item, index) => (
      <DropdownRow 
        key={uuidv4()}
        title={item} 
        typeOfHeading={typeOfHeading}
        withRowOpeningOnScroll={index === DROPDOWN_ROW_INDEX_TO_BE_OPENED_BY_DEFAULT ? false : withRowOpeningOnScroll}
        isOpenedByDefault={index === DROPDOWN_ROW_INDEX_TO_BE_OPENED_BY_DEFAULT}
      >
        {dropdownContentArray[index]}
      </DropdownRow>))
  , [dropdownHeadingsArray]);
  return(
    <>
      {dropdownItems}
    </>
  )
};

export default DropdownOrganism;
