import React from 'react';
import styled, {css} from 'styled-components';
import { Typography, Flex, Icon } from '../../atoms';
import { ReactComponent as Quotes } from '../../../assets/quotes.svg';
import { ReactComponent as Pointer } from '../../../assets/greyPointer.svg';
import { links } from '../../../utils';

const SizedFlex = styled(Flex)`
  ${({theme: {sizing}}) => css`
    width: calc((${sizing.getInTouch} + 20px));
  `}
`;

const StyledTypography = styled(Typography)`
  ${({theme: {color}}) => css`
    border-bottom: 1px solid ${color.darkBlue};
  `}
`;

const CommentSlide = ({items}) => {
  return(
    <SizedFlex column>
      <Icon 
        SVG={Quotes} 
        width='48' 
        height='39' 
        marginBottom='xl4'
      />
      <StyledTypography 
        family='sauceOne' 
        color='darkBlue' 
        size='xs' 
        paddingBottom='xl4' 
        marginBottom='xl4'
      >
        {items.description}
        <Typography 
          as='a' 
          href={links.clutch} 
          family='sauceOne' 
          color='grey' 
          size='xs'
        >
          {items.link}
        </Typography>
        <Icon 
          SVG={Pointer} 
          width='14' 
          height='14' 
          marginLeft='sm'
        />
      </StyledTypography>
      <Typography 
        family='sauceOne' 
        color='grey' 
        size='xs' 
        transform='uppercase'
      >
        {items.person}
      </Typography>
      <Typography 
        family='sauceOne' 
        color='grey' 
        size='xs'
      >
        {items.work}
      </Typography>
    </SizedFlex>
  )
};

export default CommentSlide;
