import React from 'react';
import styled, {css} from 'styled-components';
import {DropdownOrganism} from '../';
import { Typography, Flex } from '../../atoms';
import { text } from '../../../utils';
import {media} from '../../../mixins';

const {desctopLG} = media;

const SECTION_KEYS = ['first', 'second', 'third', 'forth'];

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;

const SizedTypography = styled(Typography)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.dropdownAbout};
  `}
`;

const SizedFlex = styled(Flex)`
  ${() => css`
    width: 35vw;
  `}
`;

const returnDropdownHeadings = (key) => text.mainPage.aboutSection.dropdown[key].title;
const returnDropdownContent = (key) =>  <SizedTypography 
                                          family='sauceOne' 
                                          color='darkBlue' 
                                          paddingBottom='xl7' 
                                          size='xs'
                                        >
                                          {text.mainPage.aboutSection.dropdown[key].description}
                                        </SizedTypography>

const AboutOrganism = () => {
  return(
    <Container 
      paddingX='xl11' 
      marginY='xl8' 
      justifyBetween
    >
      <Flex>
        <Typography 
          family='sauceSans' 
          color='darkBlue' 
          transform='uppercase' 
          size='xs'
        >
          {text.mainPage.aboutSection.title}
        </Typography>
      </Flex>
      <SizedFlex column>
        <SizedTypography 
          family='sauceOne' 
          color='darkBlue' 
          size='xs' 
          paddingBottom='xl8'
        >
          {text.mainPage.aboutSection.description}
        </SizedTypography>
        <DropdownOrganism
          typeOfHeading='AboutHeading'
          dropdownHeadingsArray={SECTION_KEYS.map(item => returnDropdownHeadings(item))}
          dropdownContentArray={SECTION_KEYS.map(item => returnDropdownContent(item))}
        />
      </SizedFlex>
    </Container>
  )
};

export default AboutOrganism;
