import React from 'react';
import styled, {css} from 'styled-components';
import { v4 as uuidv4 } from 'uuid';
import DropdownOrganism from '../DropdownOrganism';
import { Typography, Flex, Icon } from '../../atoms';
import { text } from '../../../utils';
import WeDoIcons from '../../../assets';
import {media} from '../../../mixins';

const {desctopLG} = media;

const SECTION_KEYS = ['first', 'second', 'third', 'forth'];
const NAMED_KEYS = [{index: 'first', iconsName: 'ui/ux'}, 
                    {index: 'second', iconsName: 'frontend'}, 
                    {index: 'third', iconsName: 'backend'}, 
                    {index: 'forth', iconsName: 'mobile'}];

const StyledDiv = styled.div`
  ${() => css`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-bottom: 48px;
    margin-right: 85px;
  `}
`;

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;

const SizedFlex = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.dropdownWeDo};
  `}
`;

const SizedTypography = styled(Typography)`
  ${() => css`
    width: 48.5vw;
    @media(max-width: 1000px){
    width: 60vw;
    }
  `}
`;

const returnDropdownHeadings = (key) => text.mainPage.doSection.dropdown[key].title;

const returnDropdownContent = (key, iconsName) => {
  return(
    <Flex column>
      <SizedTypography 
        family='sauceOne' 
        color='darkBlue' 
        paddingBottom='xl7' 
        size='xs'
      >
        {text.mainPage.doSection.dropdown[key].description}
      </SizedTypography>
      <SizedFlex wrapFlex>
        {WeDoIcons[iconsName].map(item => 
          <StyledDiv key={uuidv4()}>
            <Icon 
              SVG={item.image} 
              width='60' 
              height='60'
            />
            <Typography
              marginTop='md' 
              family='sauceOne' 
              color='darkBlue' 
              size='xs'
            >
              {item.text}
            </Typography>
          </StyledDiv>
        )}
      </SizedFlex>
    </Flex>
)};

const WhatWeDoOrganism = () => {
  return(
    <Container paddingX='xl11' paddingTop='xl7'>
      <Flex column>
        <Typography 
          family='sauceSans' 
          color='darkBlue'  
          transform='uppercase' 
          size='xs' 
          marginBottom='xl10'
        >
          {text.mainPage.doSection.title}
        </Typography>
        <DropdownOrganism 
          typeOfHeading='WeDoHeading'
          dropdownHeadingsArray={SECTION_KEYS.map(item => returnDropdownHeadings(item))}
          dropdownContentArray={NAMED_KEYS.map(item => returnDropdownContent(item.index, item.iconsName))}
        />
      </Flex>
    </Container>
)};

export default WhatWeDoOrganism;
