import React from 'react';
import styled, {css} from 'styled-components';
import { v4 as uuidv4 } from 'uuid';
import { DropdownOrganism } from '..';
import { Typography, Flex, Image } from '../../atoms';
import { text } from '../../../utils';
import fused from '../../../assets/fusedSports.png';
import imirror from '../../../assets/imirror.png';
import lumio from '../../../assets/lumio.png';
import {media} from '../../../mixins';

const {desctopLG} = media;

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;

const PhotoContainer = styled(Flex)`
  ${() => css`
    max-width: 100vw;
  `}
`;

const StyledImage = styled(Image)`
  ${() => css`
    background-position: center;
    @media(max-width: 1000px){
      height: 680px;
    }
  `}
`;

const SizedTypography = styled(Typography)`
  ${({theme: {sizing}, length}) => css`
    width: ${sizing[length]};
  `}
`;

const StyledTypography = styled(Typography)`
  ${({theme: {color}}) => css`
    border-bottom: 1px solid ${color.darkBlue};
  `}
`;

// TODO: replace duplication for the last key 
const SECTION_KEYS = ['first', 'second', 'third', 'forth'];
// TODO: replace duplication for the last key 
const NAMED_KEYS = [
  {index: 'first', imageName: fused}, 
  {index: 'second', imageName: imirror}, 
  {index: 'third', imageName: lumio},
  {index: 'forth', imageName: lumio}
];

const textByKeys = {
  "first": {
    sidepoints : [
      text.projectPage.dropdown.first.sidePoints.first,
      text.projectPage.dropdown.first.sidePoints.second,
      text.projectPage.dropdown.first.sidePoints.third
    ],
    description: [
      text.projectPage.dropdown.first.description.first,
      text.projectPage.dropdown.first.description.second,
    ]
  },
  "second" : {
    sidepoints : [
      text.projectPage.dropdown.second.sidePoints.first,
      text.projectPage.dropdown.second.sidePoints.second,
      text.projectPage.dropdown.second.sidePoints.third
    ],
    description: [
      text.projectPage.dropdown.second.description.first,
      text.projectPage.dropdown.second.description.second,
    ]
  },
  "third" : {
    sidepoints: [
      text.projectPage.dropdown.third.sidePoints.first,
      text.projectPage.dropdown.third.sidePoints.second,
      text.projectPage.dropdown.third.sidePoints.third
    ],
    description: [
      text.projectPage.dropdown.third.description.first,
      text.projectPage.dropdown.third.description.second,
    ]
  },
  "forth" : {
    sidepoints: [
      text.projectPage.dropdown.third.sidePoints.first,
      text.projectPage.dropdown.third.sidePoints.second,
      text.projectPage.dropdown.third.sidePoints.third
    ],
    description: [
      text.projectPage.dropdown.third.description.first,
      text.projectPage.dropdown.third.description.second,
    ]  
  }};

const returnDropdownHeadings = (key) => text.projectPage.dropdown[key].title;

const returnDropdownContent = (key, imageName) => {
  return(
    <Flex column>
      <Flex>
        <Flex column marginRight='xl12'>
          {textByKeys[key].sidepoints.map(item => (
            <Typography 
              family='sauceOne' 
              color='grey' 
              size='xs' 
              marginBottom='xl' 
              key={uuidv4()}
            >
            {item}
          </Typography>
          ))}              
        </Flex>
        <Flex column>
          {textByKeys[key].description.map(item => (
            <SizedTypography 
              family='sauceOne' 
              color='darkBlue' 
              size='xs' 
              length='dropdownAbout' 
              marginBottom='xl' 
              key={uuidv4()}
            >
              {item}
            </SizedTypography>
            ))}              
        </Flex>
      </Flex>
    <PhotoContainer marginX='mxl11'>
      <StyledImage 
        src={imageName} 
        width='100%' 
        height='904px'
        marginTop='xl5' 
        marginBottom='xl11'
      />
    </PhotoContainer>
    </Flex>
)};

const ProjectsOrganism = () => {
  return(
    <Container 
      column 
      paddingX='xl11' 
      paddingTop='xl7' 
      marginBottom='xl11'
    >
      <StyledTypography 
        family='sauceSans' 
        color='darkBlue'  
        transform='uppercase' 
        size='xs' 
        paddingBottom='xl5'
      >
        {text.projectPage.title}
      </StyledTypography>
      <DropdownOrganism
        typeOfHeading='WeDoHeading'
        dropdownHeadingsArray={SECTION_KEYS.map(item => returnDropdownHeadings(item))}
        dropdownContentArray={NAMED_KEYS.map(item => returnDropdownContent(item.index, item.imageName))}
        withRowOpeningOnScroll={true}
      />
    </Container>
    )
}

export default ProjectsOrganism;
