import React from 'react';
import styled, {css} from 'styled-components';
import {TitleWithLink} from '../index';
import {Typography, Flex, Icon} from '../../atoms';
import {text, routes} from '../../../utils';
import { ReactComponent as Pointer } from '../../../assets/pointer.svg';

const SizedText = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.getInTouch};  
  `}
`;

const SizedFlex = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};  
  `}
`;

const GetInTouch = ({handleRedirectToPage}) => {
  return(
    <SizedFlex 
      column 
      paddingTop='xl13' 
      paddingBottom='xl14' 
      paddingX='xl11' 
    >
      <Flex>
        <TitleWithLink 
          handleRedirectToPage={handleRedirectToPage} 
          link={routes.sayHi} 
          content={text.getInTouch.title} 
          color='white'
        >
          <Icon 
              SVG={Pointer} 
              width='5vw' 
              height='4.5vw' 
              marginLeft='xl5'
            />
        </TitleWithLink>
      </Flex>
        <SizedText 
          alignEnd 
          marginLeft='xl14'
          paddingLeft='xl11' 
          marginTop='xl3'
        >
          <Typography 
            family='sauceOne' 
            color='white' 
            size='xs'
          >
            {text.getInTouch.description}
          </Typography>
        </SizedText>
    </SizedFlex>
  )
};

export default GetInTouch;
