import React from 'react';
import styled, {css} from 'styled-components';
import { v4 as uuidv4 } from 'uuid';
import {Typography, Flex, Image} from '../../atoms';
import {WeOffer} from '../../molecules';
import weOffer from '../../../assets/weOffer.png'
import { text } from '../../../utils';
import {media} from '../../../mixins';

const {desctopLG} = media;

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;

const SizedFlex = styled(Flex)`
  ${() => css`
    width: 33vw;
  `}
`;

const SizedTypography = styled(Typography)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.dropdownAbout};
  `}
`;

const items = [
  {
    title: text.mainPage.offerSection.cards.first.title,
    description: text.mainPage.offerSection.cards.first.description
  },
  {
    title: text.mainPage.offerSection.cards.second.title,
    description: text.mainPage.offerSection.cards.second.description
  },
  {
    title: text.mainPage.offerSection.cards.third.title,
    description: text.mainPage.offerSection.cards.third.description
  },
  {
    title: text.mainPage.offerSection.cards.forth.title,
    description: text.mainPage.offerSection.cards.forth.description
  }
];

const WeOfferOrganism = () => {
  return(
    <Container 
      paddingX='xl11' 
      marginTop='xl111' 
      justifyBetween
    >
      <Flex>
        <Typography 
          family='sauceSans' 
          color='darkBlue' 
          size='xs' 
          transform='uppercase'
        >
          {text.mainPage.offerSection.title}
        </Typography>
      </Flex>
      <SizedFlex column>
        <SizedTypography 
          family='sauceOne' 
          color='darkBlue' 
          size='xs' 
          marginBottom='xl7'
        >
          {text.mainPage.offerSection.description}
        </SizedTypography>
        <Image 
          src={weOffer} 
          width='55vw' 
          height='22.5vw' 
          marginBottom='xl111'
        />
        {items.map(item => 
          <WeOffer 
            title={item.title} 
            description={item.description} 
            key={uuidv4()}/>
        )}
      </SizedFlex>
    </Container>
  )
};

export default WeOfferOrganism;
