import React, { useState, useEffect } from "react";
import {useHistory} from 'react-router-dom';
import styled, { css } from 'styled-components';
import {MenuModal} from '.';
import { Typography, Flex } from "../atoms";
import ModalWithGradientTemplate from "../templates/ModalWithGradientTemplate";

const SizedText = styled(Flex)`
  ${({theme: {sizing}}) => css`
    width: ${sizing.thanksText};  
    height: 50vh;
  `}
`;

const TeamPage = () => {
  const [isMenuClosed, setState] = useState(true);
  const history = useHistory();

  const handleRedirectAndOpenMenuModal = (url) => () => {
    setState(!isMenuClosed);
    if(url) {
      history.push(url);
    }
  }

  return(
    <>
    { isMenuClosed ? 
      <ModalWithGradientTemplate 
        isFooterShow={true} 
        handleOpenMenuModal={handleRedirectAndOpenMenuModal} 
        isThankYouModal={false}
        isTeamPage={true}
      >
        <SizedText 
          margin='auto'
          itemsCenter
          lineHeight='sm' 
          paddingBottom='xl9'
        >
          <Typography
            margin='auto'
            transform='uppercase'
            color='white'
            size='xl'
            family='sauceSans'>Comming <em>Soon!</em>
          </Typography>
        </SizedText>
      </ModalWithGradientTemplate>
      : <MenuModal 
          handleOpenMenuModal={handleRedirectAndOpenMenuModal} 
          handleRedirectToPage={handleRedirectAndOpenMenuModal}
        /> 
    }
    </> 
  )
}

export default TeamPage;
