import React from 'react';
import { bool, func } from 'prop-types';
import styled, {css} from 'styled-components';
import { Button, Icon, Flex, Typography } from '../../atoms';
import { ReactComponent as Logo } from '../../../assets/logo.svg';
import { ReactComponent as Menu } from '../../../assets/frame.svg';
import { ReactComponent as LogoWhite } from '../../../assets/logo-white.svg';
import { ReactComponent as Close } from '../../../assets/close.svg';
import { ReactComponent as CloseSayHi } from '../../../assets/closeBlack.svg';
import { ReactComponent as MenuWh } from '../../../assets/menuWhite.svg';
import {routes, text} from '../../../utils';
import {media} from '../../../mixins';

const {desctopLG, tabletLG,tabletSM, mobile} = media;

const MediaIcon = styled(Icon)`
  ${() => css`
    ${mobile}{
      width: 94px;
      height: 32px;
    }
    ${tabletLG}{
      width: 100px;
      height: 35px;
    }
    ${desctopLG}{
      width:127px;
      height: 58px;
    }
  `}
`;

const MediaButton = styled(Button)`
  ${() => css`
    ${tabletSM}{
      padding: 10px 46px 10px 46px;
    }
    ${desctopLG}{
      padding: 16px 72px 16px 72px;
    }
  `}
`;

const MainHeader = ({
  isSayHiButtonShown, 
  isSayHiModal,
  handleOpenMenuModal, 
  handleRedirectToPage, 
  isIconClickable
}) => {
  return(
    <Flex itemsCenter paddingTop='xl4'>
      <Flex>
        <Button onClick={isIconClickable ? handleRedirectToPage(routes.mainPage) : () => {} }>
          <MediaIcon 
            SVG={Logo} 
            width='117' 
            height='40'
          />
        </Button>
      </Flex>
      <Flex itemsCenter justifyEnd>
        {isSayHiButtonShown && 
          <MediaButton 
            paddingY='xl' 
            paddingX='xl9' 
            marginRight='xl6' 
            color='redSalmon' 
            onClick={handleRedirectToPage(routes.sayHi)}
          >
            <Typography color='white' size='xs'>
              {text.button}
            </Typography>
          </MediaButton>
        }
        <Button onClick={isSayHiModal ? handleOpenMenuModal(routes.mainPage) : handleOpenMenuModal()}>
          <Icon 
            SVG={isSayHiModal? CloseSayHi : Menu} 
            width='55' 
            height='40'
          />
        </Button>
      </Flex>
    </Flex>
  )
};

const SubHeader = ({
  handleOpenMenuModal, 
  isThankYouModal,
  isTeamPage,
}) => {
  return(
    <Flex 
      justifyBetween 
      itemsCenter 
      paddingTop='xl4'
    >
      <Icon 
        SVG={LogoWhite} 
        width='117' 
        height='40'/>
      <Button onClick={isThankYouModal ? (handleOpenMenuModal) : (handleOpenMenuModal())}>
        <Icon 
          SVG={isTeamPage? MenuWh : Close} 
          width='40' 
          height='20'
        />
      </Button>
    </Flex>
  )
};

MainHeader.propTypes = {
  isSayHiButtonShown: bool, 
  handleOpenMenuModal: func,
  handleRedirectToPage: func,
};

SubHeader.propTypes = {
  handleOpenMenuModal: func,
};

const Header = {
  MainHeader,
  SubHeader,
};

export default Header;
