import { oneOf } from 'prop-types';
import styled, { css } from 'styled-components';
import { marginMixin } from '../../../mixins';

const {
  margin,
  marginTop,
  marginRight,
  marginBottom,
  marginLeft,
  marginX,
  marginY,
} = marginMixin;

const Input = styled.input`
  ${({
    family,
    size,
    theme: {
      color, 
      typography,
    },
  }) => css`
    ${margin}
    ${marginTop}
    ${marginRight}
    ${marginBottom}
    ${marginLeft}
    ${marginX}
    ${marginY}

    min-width: 100%;
    height: 41px;
    border: none;
    border-bottom: 1px solid ${color.blueText};
    outline: none;

    ::placeholder {
      font-size: ${typography.size[size]};
      font-family: ${typography.family.sauceOne};
      color: ${color.grey};
    }
  `}
`;

Input.defaultProps = {
  type: 'text',
  onChange: () => null,
};

Input.propTypes = {
  type: oneOf([
    'email',
    'text',
  ]),
};

export default Input;
