import React from 'react';
import styled, {css} from 'styled-components'
import { func } from 'prop-types';
import { Typography, Button, Flex } from '../../atoms';
import {routes, text} from '../../../utils';

const SizedLink = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.thanksLink};  
  `}
`;

const ThanksTitle = () => {
  return (
    <>
      <Typography 
        transform="uppercase" 
        color="white" 
        size="xl" 
        family='sauceSans' 
        marginTop='xl10'
      >
        {text.thankModule.title.firstLine.normal} <em>{text.thankModule.title.firstLine.cursive}</em>
      </Typography>
      <Typography 
        align="right" 
        transform="uppercase" 
        color="white" 
        size="xl" 
        family='sauceSans'
      >
        {text.thankModule.title.secondLine.normal} <em>{text.thankModule.title.secondLine.cursive}</em>
      </Typography>
      <Typography 
        transform="uppercase" 
        color="white" 
        size="xl" 
        family='sauceSans'
      >
        <em>{text.thankModule.title.thirdLine.cursive}</em>
      </Typography>
    </>
  )
};

const ThanksLink = ({handleRedirectToPage}) => {
  return(
    <Flex 
      paddingTop="xl7" 
      justifyEnd 
      marginRight="xl7"
    >
      <SizedLink column itemsStart>
        <Typography 
          color="white" 
          size="xs" 
          family='sauceOne'
        >
          {text.thankModule.description.text}
        </Typography>
        <Button onClick={handleRedirectToPage(routes.mainPage)}>
          <Typography 
            size='xs' 
            color='white' 
            decoration="underline"
          >
            {text.thankModule.description.link}
          </Typography>
        </Button>
      </SizedLink>
    </Flex>
  )
};

ThanksLink.propTypes = {
  handleRedirectToPage: func,
};

const ThanksCenter = {
  ThanksTitle,
  ThanksLink,
};

export default ThanksCenter;
