import styled, { css } from 'styled-components';
import { marginMixin, paddingMixin, lineHeight } from '../../../mixins/index';

const {
  margin,
  marginTop,
  marginRight,
  marginBottom,
  marginLeft,
  marginX,
  marginY,
} = marginMixin;

const {
  padding,
  paddingTop,
  paddingRight,
  paddingBottom,
  paddingLeft,
  paddingX,
  paddingY,
} = paddingMixin;


const Typography = styled.p`
  ${({
    opacity,
    family,
    decoration,
    align,
    transform,
    color,
    size,
    theme: {
      color: colorFromTheme,
      typography,
    }}) => css`
    ${margin}
    ${marginTop}
    ${marginRight}
    ${marginBottom}
    ${marginLeft}
    ${marginX}
    ${marginY}

    ${padding}
    ${paddingTop}
    ${paddingRight}
    ${paddingBottom}
    ${paddingLeft}
    ${paddingX}
    ${paddingY}

    ${lineHeight}
    ${align && `
      text-align: ${align};`}
    ${transform && `
      text-transform: ${transform};`}
    ${color && `
      color: ${colorFromTheme[color]};
    `}
    ${decoration && `
      text-decoration: ${decoration};
    `}
    ${opacity && `
      opacity: ${opacity};
    `}
    font-size: ${typography.size[size]};
    font-family: ${typography.family[family]};
  `}
`;


export default Typography;
