import styled, { css } from 'styled-components';
import { marginMixin, paddingMixin } from '../../../mixins';

const {
  margin,
  marginTop,
  marginRight,
  marginBottom,
  marginLeft,
  marginX,
  marginY,
} = marginMixin;

const {
  padding,
  paddingTop,
  paddingRight,
  paddingBottom,
  paddingLeft,
  paddingX,
  paddingY,
} = paddingMixin;

const Button = styled.button`
  ${({
    color,
    theme: {
      color: colorFromTheme,
      typography,
      borderRounded,
    } }) => css`
    ${margin}
    ${marginTop}
    ${marginRight}
    ${marginBottom}
    ${marginLeft}
    ${marginX}
    ${marginY}

    ${padding}
    ${paddingTop}
    ${paddingRight}
    ${paddingBottom}
    ${paddingLeft}
    ${paddingX}
    ${paddingY}

    color: ${colorFromTheme.white};
    border-radius: ${borderRounded};
    border: none;
    font-size: ${typography.size.xs};
    font-family: ${typography.family.sauceSans};
    background-color: ${color ? colorFromTheme[color] : 'transparent'};
    outline: none;
  `}
`;

export default Button;
