import React from 'react';
import styled, {css} from 'styled-components';
import { Typography, Flex, Icon, Button } from '../../atoms';
import { ReactComponent as Left } from '../../../assets/leftArrow.svg';
import { ReactComponent as Right } from '../../../assets/rightArrow.svg';

const Container = styled(Flex)`
  ${({theme: {sizing}, border}) => css`
    max-width: ${sizing.mainContainer};
    border-bottom: ${border};
  `}
`;

const HeaderWithArrows = ({
  content, 
  leftBtn, 
  rightBtn, 
  border
}) => {
  return(
    <Container 
      marginBottom='xl8' 
      justifyBetween 
      border={border} 
      paddingBottom='xl4'
    >
      <Flex>
        <Typography 
          family='sauceSans' 
          color='darkBlue'  
          transform='uppercase' 
          size='xs'
        >
          {content}
        </Typography>
      </Flex>
      <Flex justifyEnd>
        <Button onClick={leftBtn}>
          <Icon 
            SVG={Left} 
            width='29' 
            height='29'
          />
        </Button>
        <Button marginLeft='xl8' onClick={rightBtn}>
          <Icon 
            SVG={Right} 
            width='29' 
            height='29'
          />
        </Button>
      </Flex>
    </Container>
  )
};

export default HeaderWithArrows;
