import React from 'react';
import styled, {css} from 'styled-components';
import {Flex} from '../../atoms';
import {SayHiBlock} from '../../molecules';
import {media} from '../../../mixins';

const {desctopLG} = media;

const Container = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.mainContainer};
    ${desctopLG}{
      margin-left: auto;
      margin-right: auto;
    }
  `}
`;

const SayHiOrganism = ({isThanksClosed, setThanks}) => {
  return(
    <Container 
      justifyBetween 
      marginTop='xl10' 
      paddingX='xl11'
    >
      <SayHiBlock.SideBlock/>
      <SayHiBlock.MainBlock 
        isThanksClosed={isThanksClosed} 
        setThanks={setThanks}
      />
    </Container>
  )
}


export default SayHiOrganism;
