import React from 'react';
import styled, {css} from 'styled-components';
import { v4 as uuidv4 } from 'uuid';
import {Typography, Flex} from '../../atoms';
import {links, text} from '../../../utils';

const SizedFlex = styled(Flex)`
  ${({theme: {sizing}}) => css`
    max-width: ${sizing.sayHiFooter};
  `}
`
const items = [
  {
    link: links.email,
    content: text.footer.links.email
  },
  {
    link: links.linkedIn,
    content: text.footer.links.linkedIn
  },
  {
    link: links.facebook,
    content: text.footer.links.facebook
  },
  {
    link: links.instagram,
    content: text.footer.links.instagram
  }
];


const Footer = () => {
  return(
    <Flex 
      row 
      justifyBetween 
      marginTop='xl6' 
      paddingBottom='sm'
    >
      <Flex itemsCenter>
        <Typography 
          color='white' 
          opacity='0.5' 
          sixe='xxxs' 
          family='sauceOne' 
          url='urlOne'
        >
          {text.footer.rights}
        </Typography>
      </Flex>
      <SizedFlex justifyBetween itemsCenter>
        { items.map(item =>
          <Typography 
            key={uuidv4()} 
            as="a" 
            href={item.link} 
            color='white' 
            size='xs' 
            decoration='none' 
            family='sauceOne'
          >
            {item.content}
          </Typography>
        )}
      </SizedFlex>
    </Flex>
  )
};

export default Footer;
