import React from 'react';
import { Typography, Icon, Image } from '../../atoms';
import { links } from '../../../utils';
import { ReactComponent as Pointer } from '../../../assets/blackPointer.svg';

const BlogSlide = ({items}) => {
  return(
    <>
      <Image 
        src={items.image} 
        width='100%' 
        height='100%' 
        marginBottom='xl4'
      />
      <Typography 
        as='a' 
        href={links.blog} 
        family='sauceOne' 
        color='darkBlue' 
        size='xs' 
        decoration='none'
      >
        {items.link}
        <Icon 
          SVG={Pointer} 
          width='14' 
          height='14' 
          marginLeft='sm'
        />
      </Typography>
    </>
  )
};

  export default BlogSlide;
